
public class Atividade0a100 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int soma;
		soma = 0;
		
		System.out.println("As somas dos números de 1 a 100 é: ");
		
		for (int i = 1; i<=100; i++) {
			soma += i;
		}
		System.out.println(soma);
	}

}
